import 'package:flutter/material.dart';\n\nclass EarnScreen extends StatelessWidget {\n  const EarnScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text('Nexora Earn'),\n        actions: [\n          Container(\n            margin: const EdgeInsets.only(right: 16),\n            padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 6),\n            decoration: BoxDecoration(\n              color: const Color(0xFFFFD700).withOpacity(0.2),\n              borderRadius: BorderRadius.circular(20),\n            ),\n            child: const Row(\n              children: [\n                Icon(Icons.monetization_on, color: Color(0xFFFFD700), size: 20),\n                SizedBox(width: 4),\n                Text(\n                  '1,250',\n                  style: TextStyle(\n                    color: Color(0xFFFFD700),\n                    fontWeight: FontWeight.bold,\n                  ),\n                ),\n              ],\n            ),\n          ),\n        ],\n      ),\n      body: SingleChildScrollView(\n        child: Padding(\n          padding: const EdgeInsets.all(16.0),\n          child: Column(\n            crossAxisAlignment: CrossAxisAlignment.start,\n            children: [\n              // VIP Upgrade Card\n              Container(\n                width: double.infinity,\n                padding: const EdgeInsets.all(20),\n                decoration: BoxDecoration(\n                  gradient: const LinearGradient(\n                    colors: [Color(0xFFFFD700), Color(0xFFFFA500)],\n                    begin: Alignment.topLeft,\n                    end: Alignment.bottomRight,\n                  ),\n                  borderRadius: BorderRadius.circular(16),\n                ),\n                child: Row(\n                  children: [\n                    const Icon(Icons.diamond, color: Colors.white, size: 40),\n                    const SizedBox(width: 16),\n                    Expanded(\n                      child: Column(\n                        crossAxisAlignment: CrossAxisAlignment.start,\n                        children: [\n                          const Text(\n                            'Upgrade to VIP',\n                            style: TextStyle(\n                              color: Colors.white,\n                              fontSize: 18,\n                              fontWeight: FontWeight.bold,\n                            ),\n                          ),\n                          const SizedBox(height: 4),\n                          const Text(\n                            'Earn 2Ã— rewards on everything',\n                            style: TextStyle(\n                              color: Colors.white70,\n                              fontSize: 13,\n                            ),\n                          ),\n                        ],\n                      ),\n                    ),\n                    ElevatedButton(\n                      onPressed: () {},\n                      style: ElevatedButton.styleFrom(\n                        backgroundColor: Colors.white,\n                        foregroundColor: const Color(0xFFFFD700),\n                        padding: const EdgeInsets.symmetric(horizontal: 20, vertical: 10),\n                      ),\n                      child: const Text('Upgrade'),\n                    ),\n                  ],\n                ),\n              ),\n              const SizedBox(height: 24),\n              // Daily Rewards\n              const Text(\n                'Daily Rewards',\n                style: TextStyle(\n                  fontSize: 18,\n                  fontWeight: FontWeight.bold,\n                ),\n              ),\n              const SizedBox(height: 12),\n              _buildDailyReward('Check-in Bonus', 'Login today', '50', Icons.login, true),\n              _buildDailyReward('Daily Challenge', '3/5 tasks completed', '100', Icons.assignment_turned_in, false),\n              _buildDailyReward('Spin the Wheel', 'Try your luck', '???', Icons.casino, false),\n              const SizedBox(height: 24),\n              // Earn More\n              const Text(\n                'Earn More Coins',\n                style: TextStyle(\n                  fontSize: 18,\n                  fontWeight: FontWeight.bold,\n                ),\n              ),\n              const SizedBox(height: 12),\n              Row(\n                children: [\n                  Expanded(\n                    child: _buildEarnCard(\n                      'Watch Videos',\n                      '5 coins per video',\n                      Icons.play_circle_filled,\n                      Colors.red,\n                    ),\n                  ),\n                  const SizedBox(width: 12),\n                  Expanded(\n                    child: _buildEarnCard(\n                      'Watch Ads',\n                      '10 coins per ad',\n                      Icons.ads_click,\n                      Colors.blue,\n                    ),\n                  ),\n                ],\n              ),\n              const SizedBox(height: 12),\n              Row(\n                children: [\n                  Expanded(\n                    child: _buildEarnCard(\n                      'Refer Friends',\n                      '100 coins each',\n                      Icons.people,\n                      Colors.green,\n                    ),\n                  ),\n                  const SizedBox(width: 12),\n                  Expanded(\n                    child: _buildEarnCard(\n                      'Complete Tasks',\n                      'Up to 500 coins',\n                      Icons.task_alt,\n                      Colors.orange,\n                    ),\n                  ),\n                ],\n              ),\n              const SizedBox(height: 24),\n              // Referral Section\n              Container(\n                padding: const EdgeInsets.all(20),\n                decoration: BoxDecoration(\n                  color: const Color(0xFF6C63FF).withOpacity(0.1),\n                  borderRadius: BorderRadius.circular(16),\n                  border: Border.all(color: const Color(0xFF6C63FF).withOpacity(0.3)),\n                ),\n                child: Column(\n                  children: [\n                    const Row(\n                      children: [\n                        Icon(Icons.card_giftcard, color: Color(0xFF6C63FF)),\n                        SizedBox(width: 12),\n                        Expanded(\n                          child: Column(\n                            crossAxisAlignment: CrossAxisAlignment.start,\n                            children: [\n                              Text(\n                                'Your Referral Code',\n                                style: TextStyle(\n                                  fontWeight: FontWeight.bold,\n                                  fontSize: 16,\n                                ),\n                              ),\n                              Text(\n                                'Share and earn 100 coins per friend',\n                                style: TextStyle(\n                                  fontSize: 12,\n                                  color: Colors.grey,\n                                ),\n                              ),\n                            ],\n                          ),\n                        ),\n                      ],\n                    ),\n                    const SizedBox(height: 16),\n                    Container(\n                      padding: const EdgeInsets.symmetric(horizontal: 16, vertical: 12),\n                      decoration: BoxDecoration(\n                        color: Colors.white,\n                        borderRadius: BorderRadius.circular(12),\n                      ),\n                      child: Row(\n                        mainAxisAlignment: MainAxisAlignment.spaceBetween,\n                        children: [\n                          const Text(\n                            'NEXORA2024',\n                            style: TextStyle(\n                              fontSize: 18,\n                              fontWeight: FontWeight.bold,\n                              letterSpacing: 2,\n                            ),\n                          ),\n                          IconButton(\n                            onPressed: () {},\n                            icon: const Icon(Icons.copy),\n                            color: const Color(0xFF6C63FF),\n                          ),\n                        ],\n                      ),\n                    ),\n                    const SizedBox(height: 12),\n                    ElevatedButton.icon(\n                      onPressed: () {},\n                      icon: const Icon(Icons.share),\n                      label: const Text('Share with Friends'),\n                      style: ElevatedButton.styleFrom(\n                        backgroundColor: const Color(0xFF6C63FF),\n                        foregroundColor: Colors.white,\n                        padding: const EdgeInsets.symmetric(horizontal: 24, vertical: 12),\n                        shape: RoundedRectangleBorder(\n                          borderRadius: BorderRadius.circular(12),\n                        ),\n                      ),\n                    ),\n                  ],\n                ),\n              ),\n              const SizedBox(height: 24),\n              // Withdraw Section\n              Container(\n                padding: const EdgeInsets.all(20),\n                decoration: BoxDecoration(\n                  color: Colors.grey[50],\n                  borderRadius: BorderRadius.circular(16),\n                ),\n                child: Row(\n                  children: [\n                    const Expanded(\n                      child: Column(\n                        crossAxisAlignment: CrossAxisAlignment.start,\n                        children: [\n                          Text(\n                            'Ready to cash out?',\n                            style: TextStyle(\n                              fontSize: 16,\n                              fontWeight: FontWeight.bold,\n                            ),\n                          ),\n                          SizedBox(height: 4),\n                          Text(\n                            'Convert coins to real money',\n                            style: TextStyle(\n                              fontSize: 12,\n                              color: Colors.grey,\n                            ),\n                          ),\n                        ],\n                      ),\n                    ),\n                    ElevatedButton(\n                      onPressed: () {},\n                      style: ElevatedButton.styleFrom(\n                        backgroundColor: const Color(0xFF03DAC6),\n                        foregroundColor: Colors.white,\n                        padding: const EdgeInsets.symmetric(horizontal: 20, vertical: 12),\n                      ),\n                      child: const Text('Withdraw'),\n                    ),\n                  ],\n                ),\n              ),\n            ],\n          ),\n        ),\n      ),\n    );\n  }\n\n  Widget _buildDailyReward(String title, String subtitle, String coins, IconData icon, bool claimed) {\n    return Container(\n      margin: const EdgeInsets.only(bottom: 12),\n      padding: const EdgeInsets.all(16),\n      decoration: BoxDecoration(\n        color: claimed ? Colors.grey[100] : Colors.white,\n        borderRadius: BorderRadius.circular(12),\n        border: Border.all(color: claimed ? Colors.grey[300]! : Colors.grey[200]!),\n      ),\n      child: Row(\n        children: [\n          Container(\n            padding: const EdgeInsets.all(10),\n            decoration: BoxDecoration(\n              color: claimed ? Colors.grey[300] : const Color(0xFFFFD700).withOpacity(0.2),\n              borderRadius: BorderRadius.circular(10),\n            ),\n            child: Icon(\n              icon,\n              color: claimed ? Colors.grey : const Color(0xFFFFD700),\n              size: 24,\n            ),\n          ),\n          const SizedBox(width: 16),\n          Expanded(\n            child: Column(\n              crossAxisAlignment: CrossAxisAlignment.start,\n              children: [\n                Text(\n                  title,\n                  style: TextStyle(\n                    fontSize: 15,\n                    fontWeight: FontWeight.w600,\n                    color: claimed ? Colors.grey : Colors.black,\n                  ),\n                ),\n                const SizedBox(height: 2),\n                Text(\n                  subtitle,\n                  style: TextStyle(\n                    fontSize: 12,\n                    color: Colors.grey[600],\n                  ),\n                ),\n              ],\n            ),\n          ),\n          Column(\n            children: [\n              Text(\n                claimed ? 'Claimed' : '+$coins',\n                style: TextStyle(\n                  fontSize: 16,\n                  fontWeight: FontWeight.bold,\n                  color: claimed ? Colors.grey : const Color(0xFFFFD700),\n                ),\n              ),\n              if (!claimed)\n                const Icon(Icons.arrow_forward_ios, size: 14, color: Colors.grey),\n            ],\n          ),\n        ],\n      ),\n    );\n  }\n\n  Widget _buildEarnCard(String title, String subtitle, IconData icon, Color color) {\n    return Container(\n      padding: const EdgeInsets.all(16),\n      decoration: BoxDecoration(\n        color: color.withOpacity(0.1),\n        borderRadius: BorderRadius.circular(12),\n        border: Border.all(color: color.withOpacity(0.3)),\n      ),\n      child: Column(\n        children: [\n          Icon(icon, color: color, size: 32),\n          const SizedBox(height: 8),\n          Text(\n            title,\n            textAlign: TextAlign.center,\n            style: const TextStyle(\n              fontSize: 14,\n              fontWeight: FontWeight.bold,\n            ),\n          ),\n          const SizedBox(height: 4),\n          Text(\n            subtitle,\n            textAlign: TextAlign.center,\n            style: TextStyle(\n              fontSize: 11,\n              color: Colors.grey[700],\n            ),\n          ),\n        ],\n      ),\n    );\n  }\n}\n