import 'package:flutter/material.dart';\n\nclass VideoScreen extends StatelessWidget {\n  const VideoScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text('Nexora Video'),\n        actions: [\n          IconButton(\n            icon: const Icon(Icons.search),\n            onPressed: () {},\n          ),\n          IconButton(\n            icon: const Icon(Icons.notifications_outlined),\n            onPressed: () {},\n          ),\n        ],\n      ),\n      body: Column(\n        children: [\n          // Category Tabs\n          Container(\n            height: 50,\n            padding: const EdgeInsets.symmetric(vertical: 8),\n            child: ListView(\n              scrollDirection: Axis.horizontal,\n              padding: const EdgeInsets.symmetric(horizontal: 16),\n              children: [\n                _buildCategoryChip('For You', true),\n                _buildCategoryChip('Following', false),\n                _buildCategoryChip('Education', false),\n                _buildCategoryChip('Entertainment', false),\n                _buildCategoryChip('Tech', false),\n                _buildCategoryChip('Business', false),\n              ],\n            ),\n          ),\n          // Video Grid\n          Expanded(\n            child: GridView.builder(\n              padding: const EdgeInsets.all(12),\n              gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(\n                crossAxisCount: 2,\n                childAspectRatio: 0.7,\n                crossAxisSpacing: 12,\n                mainAxisSpacing: 12,\n              ),\n              itemCount: 20,\n              itemBuilder: (context, index) {\n                return _buildVideoCard(index);\n              },\n            ),\n          ),\n        ],\n      ),\n      floatingActionButton: FloatingActionButton.extended(\n        onPressed: () {},\n        backgroundColor: const Color(0xFFFF0050),\n        icon: const Icon(Icons.add, color: Colors.white),\n        label: const Text(\n          'Upload',\n          style: TextStyle(color: Colors.white),\n        ),\n      ),\n    );\n  }\n\n  Widget _buildCategoryChip(String label, bool selected) {\n    return Container(\n      margin: const EdgeInsets.only(right: 8),\n      child: FilterChip(\n        label: Text(label),\n        selected: selected,\n        onSelected: (bool value) {},\n        backgroundColor: Colors.grey[100],\n        selectedColor: const Color(0xFF6C63FF),\n        labelStyle: TextStyle(\n          color: selected ? Colors.white : Colors.black,\n          fontWeight: selected ? FontWeight.bold : FontWeight.normal,\n        ),\n      ),\n    );\n  }\n\n  Widget _buildVideoCard(int index) {\n    final views = ['2.5M', '1.8M', '3.2M', '890K', '5.1M'];\n    final titles = [\n      'How to earn money online',\n      'Best coding tips 2024',\n      'Start your freelance career',\n      'Master digital marketing',\n      'Build your first app',\n    ];\n    final creators = [\n      '@techguru',\n      '@codemaster',\n      '@freelanceking',\n      '@marketingpro',\n      '@appdeveloper',\n    ];\n\n    return Container(\n      decoration: BoxDecoration(\n        borderRadius: BorderRadius.circular(12),\n        boxShadow: [\n          BoxShadow(\n            color: Colors.black.withOpacity(0.1),\n            blurRadius: 5,\n            offset: const Offset(0, 2),\n          ),\n        ],\n      ),\n      child: ClipRRect(\n        borderRadius: BorderRadius.circular(12),\n        child: Stack(\n          fit: StackFit.expand,\n          children: [\n            // Thumbnail\n            Container(\n              color: Colors.grey[300],\n              child: Icon(\n                Icons.play_circle_outline,\n                size: 60,\n                color: Colors.white.withOpacity(0.8),\n              ),\n            ),\n            // Gradient Overlay\n            Positioned(\n              bottom: 0,\n              left: 0,\n              right: 0,\n              child: Container(\n                padding: const EdgeInsets.all(12),\n                decoration: BoxDecoration(\n                  gradient: LinearGradient(\n                    begin: Alignment.bottomCenter,\n                    end: Alignment.topCenter,\n                    colors: [\n                      Colors.black.withOpacity(0.8),\n                      Colors.transparent,\n                    ],\n                  ),\n                ),\n                child: Column(\n                  crossAxisAlignment: CrossAxisAlignment.start,\n                  mainAxisSize: MainAxisSize.min,\n                  children: [\n                    Text(\n                      titles[index % titles.length],\n                      style: const TextStyle(\n                        color: Colors.white,\n                        fontSize: 13,\n                        fontWeight: FontWeight.bold,\n                      ),\n                      maxLines: 2,\n                      overflow: TextOverflow.ellipsis,\n                    ),\n                    const SizedBox(height: 4),\n                    Text(\n                      creators[index % creators.length],\n                      style: TextStyle(\n                        color: Colors.white.withOpacity(0.9),\n                        fontSize: 11,\n                      ),\n                    ),\n                    const SizedBox(height: 4),\n                    Row(\n                      children: [\n                        Icon(\n                          Icons.remove_red_eye,\n                          size: 14,\n                          color: Colors.white.withOpacity(0.8),\n                        ),\n                        const SizedBox(width: 4),\n                        Text(\n                          '${views[index % views.length]} views',\n                          style: TextStyle(\n                            color: Colors.white.withOpacity(0.8),\n                            fontSize: 11,\n                          ),\n                        ),\n                      ],\n                    ),\n                  ],\n                ),\n              ),\n            ),\n            // Duration Badge\n            Positioned(\n              top: 8,\n              right: 8,\n              child: Container(\n                padding: const EdgeInsets.symmetric(horizontal: 6, vertical: 3),\n                decoration: BoxDecoration(\n                  color: Colors.black.withOpacity(0.7),\n                  borderRadius: BorderRadius.circular(4),\n                ),\n                child: const Text(\n                  '5:23',\n                  style: TextStyle(\n                    color: Colors.white,\n                    fontSize: 11,\n                    fontWeight: FontWeight.bold,\n                  ),\n                ),\n              ),\n            ),\n            // Earning Badge\n            Positioned(\n              top: 8,\n              left: 8,\n              child: Container(\n                padding: const EdgeInsets.symmetric(horizontal: 6, vertical: 3),\n                decoration: BoxDecoration(\n                  color: const Color(0xFFFFD700).withOpacity(0.9),\n                  borderRadius: BorderRadius.circular(4),\n                ),\n                child: const Row(\n                  mainAxisSize: MainAxisSize.min,\n                  children: [\n                    Icon(Icons.monetization_on, size: 12, color: Colors.white),\n                    SizedBox(width: 2),\n                    Text(\n                      '+5',\n                      style: TextStyle(\n                        color: Colors.white,\n                        fontSize: 11,\n                        fontWeight: FontWeight.bold,\n                      ),\n                    ),\n                  ],\n                ),\n              ),\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n}\n